export class AdminDashboardComponent {
  constructor(http, authService) {
    this.http = http;
    this.authService = authService;
    this.customers = [];
    this.cleaners = [];
    this.pendingCleaners = [];
    this.bookings = [];
    this.recentBookings = [];
    this.totalUsers = 0;
    this.totalCustomers = 0;
    this.totalCleaners = 0;
    this.totalBookings = 0;
    this.completedBookings = 0;
    this.pendingBookings = 0;
    this.availableCleaners = 0;
    this.totalRevenue = 0;
    // Tab navigation
    this.activeTab = 'overview';
    // Cleaner management
    this.showCreateCleanerForm = false;
    this.showPendingCleaners = false;
    this.pendingCleanersCount = 0;
    this.isCreating = false;
    this.errorMessage = '';
    this.successMessage = '';
    this.newCleaner = {
      firstName: '',
      lastName: '',
      email: '',
      username: '',
      password: '',
      phoneNumber: '',
      address: '',
      city: '',
      region: '',
      postalCode: '',
      country: '',
      hourlyRate: 0,
      bio: '',
      profilePicture: ''
    };
  }
  ngOnInit() {
    this.loadCustomers();
    this.loadCleaners();
    this.loadBookings();
    this.loadPendingCleaners();
  }
  // Tab navigation methods
  setActiveTab(tabName) {
    console.log('Switching to tab:', tabName);
    this.activeTab = tabName;
  }
  // Authentication helper
  getAuthHeaders() {
    const token = this.authService.getToken();
    return new HttpHeaders({
      'Authorization': `Bearer ${token}`,
      'Content-Type': 'application/json'
    });
  }
  // Data loading methods
  loadCustomers() {
    this.http.get('http://localhost:8080/api/customers', {
      headers: this.getAuthHeaders()
    }).subscribe({
      next: customers => {
        this.customers = customers;
        this.totalCustomers = customers.length;
        this.updateTotalUsers();
      },
      error: error => {
        console.error('Error loading customers:', error);
      }
    });
  }
  loadCleaners() {
    this.http.get('http://localhost:8080/api/cleaners', {
      headers: this.getAuthHeaders()
    }).subscribe({
      next: cleaners => {
        this.cleaners = cleaners;
        this.totalCleaners = cleaners.length;
        this.availableCleaners = cleaners.filter(c => c.cleanerStatus === 'AVAILABLE').length;
        this.updateTotalUsers();
      },
      error: error => {
        console.error('Error loading cleaners:', error);
      }
    });
  }
  loadBookings() {
    this.http.get('http://localhost:8080/api/bookings', {
      headers: this.getAuthHeaders()
    }).subscribe({
      next: bookings => {
        this.bookings = bookings;
        this.recentBookings = bookings.slice(0, 10); // Show last 10 bookings
        this.calculateStats();
      },
      error: error => {
        console.error('Error loading bookings:', error);
      }
    });
  }
  // Statistics calculation
  updateTotalUsers() {
    this.totalUsers = this.totalCustomers + this.totalCleaners;
  }
  calculateStats() {
    this.totalBookings = this.bookings.length;
    this.completedBookings = this.bookings.filter(b => b.status === 'COMPLETED').length;
    this.pendingBookings = this.bookings.filter(b => b.status === 'PENDING').length;
    this.totalRevenue = this.bookings.filter(b => b.status === 'COMPLETED').reduce((sum, b) => sum + b.totalAmount, 0);
  }
  // Utility methods
  formatDate(dateString) {
    return new Date(dateString).toLocaleDateString();
  }
  getAverageBookingValue() {
    if (this.completedBookings === 0) return 0;
    return this.totalRevenue / this.completedBookings;
  }
  getStatusClass(status) {
    switch (status) {
      case 'PENDING':
        return 'bg-warning';
      case 'CONFIRMED':
        return 'bg-info';
      case 'IN_PROGRESS':
        return 'bg-primary';
      case 'COMPLETED':
        return 'bg-success';
      case 'CANCELLED':
        return 'bg-danger';
      case 'AVAILABLE':
        return 'bg-success';
      case 'BUSY':
        return 'bg-warning';
      case 'OFFLINE':
        return 'bg-secondary';
      case 'ON_BREAK':
        return 'bg-info';
      case 'PENDING_APPROVAL':
        return 'bg-warning';
      case 'REJECTED':
        return 'bg-danger';
      case 'ACTIVE':
        return 'bg-success';
      case 'INACTIVE':
        return 'bg-secondary';
      default:
        return 'bg-secondary';
    }
  }
  // Cleaner management methods
  loadPendingCleaners() {
    this.http.get('http://localhost:8080/api/cleaners/pending-approval', {
      headers: this.getAuthHeaders()
    }).subscribe({
      next: cleaners => {
        this.pendingCleaners = cleaners;
        this.pendingCleanersCount = cleaners.length;
        // Automatically show pending cleaners section if there are any
        if (cleaners.length > 0) {
          this.showPendingCleaners = true;
        }
      },
      error: error => {
        console.error('Error loading pending cleaners:', error);
      }
    });
  }
  createCleaner() {
    this.isCreating = true;
    this.errorMessage = '';
    this.successMessage = '';
    this.http.post('http://localhost:8080/api/cleaners/register', this.newCleaner, {
      headers: this.getAuthHeaders()
    }).subscribe({
      next: response => {
        this.isCreating = false;
        this.successMessage = 'Cleaner created successfully!';
        this.loadCleaners();
        this.closeCreateCleanerForm();
        this.resetNewCleanerForm();
        // Clear success message after 3 seconds
        setTimeout(() => {
          this.successMessage = '';
        }, 3000);
      },
      error: error => {
        this.isCreating = false;
        console.error('Error creating cleaner:', error);
        if (error.error && error.error.message) {
          this.errorMessage = error.error.message;
        } else if (error.status === 400) {
          this.errorMessage = 'Invalid data provided. Please check all fields.';
        } else if (error.status === 409) {
          this.errorMessage = 'Username or email already exists.';
        } else {
          this.errorMessage = 'Failed to create cleaner. Please try again.';
        }
      }
    });
  }
  approveCleaner(cleanerId) {
    this.errorMessage = '';
    this.successMessage = '';
    // Check if token is expired
    if (!this.authService.refreshTokenIfNeeded()) {
      this.errorMessage = 'Your session has expired. Please log in again.';
      return;
    }
    this.http.put(`http://localhost:8080/api/cleaners/${cleanerId}/approve`, {}, {
      headers: this.getAuthHeaders()
    }).subscribe({
      next: () => {
        this.successMessage = 'Cleaner approved successfully!';
        this.loadCleaners();
        this.loadPendingCleaners();
        // Clear success message after 3 seconds
        setTimeout(() => {
          this.successMessage = '';
        }, 3000);
      },
      error: error => {
        console.error('Error approving cleaner:', error);
        if (error.status === 401) {
          this.errorMessage = 'Authentication failed. Your session may have expired or there was a server configuration change. Please log in again.';
          this.authService.logout();
        } else if (error.status === 403) {
          this.errorMessage = 'You do not have permission to approve cleaners.';
        } else if (error.status === 404) {
          this.errorMessage = 'Cleaner not found.';
        } else {
          this.errorMessage = 'Failed to approve cleaner. Please try again.';
        }
      }
    });
  }
  rejectCleaner(cleanerId) {
    if (confirm('Are you sure you want to reject this cleaner application?')) {
      this.errorMessage = '';
      this.successMessage = '';
      // Check if token is expired
      if (!this.authService.refreshTokenIfNeeded()) {
        this.errorMessage = 'Your session has expired. Please log in again.';
        return;
      }
      this.http.put(`http://localhost:8080/api/cleaners/${cleanerId}/reject`, {}, {
        headers: this.getAuthHeaders()
      }).subscribe({
        next: () => {
          this.successMessage = 'Cleaner application rejected.';
          this.loadCleaners();
          this.loadPendingCleaners();
          // Clear success message after 3 seconds
          setTimeout(() => {
            this.successMessage = '';
          }, 3000);
        },
        error: error => {
          console.error('Error rejecting cleaner:', error);
          if (error.status === 401) {
            this.errorMessage = 'Authentication failed. Your session may have expired or there was a server configuration change. Please log in again.';
            this.authService.logout();
          } else if (error.status === 403) {
            this.errorMessage = 'You do not have permission to reject cleaners.';
          } else if (error.status === 404) {
            this.errorMessage = 'Cleaner not found.';
          } else {
            this.errorMessage = 'Failed to reject cleaner. Please try again.';
          }
        }
      });
    }
  }
  closeCreateCleanerForm() {
    this.showCreateCleanerForm = false;
    this.errorMessage = '';
    this.successMessage = '';
    this.resetNewCleanerForm();
  }
  resetNewCleanerForm() {
    this.newCleaner = {
