import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Customer, Cleaner, Booking } from '../../../models/user.model';
import { AuthService } from '../../../services/auth.service';

@Component({
  selector: 'app-customer-dashboard',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './customer-dashboard.component.html',
  styles: []
})
export class CustomerDashboardComponent implements OnInit {
  customer: Customer | null = null;
  bookings: Booking[] = [];
  availableCleaners: Cleaner[] = [];
  totalBookings = 0;
  pendingBookings = 0;
  completedBookings = 0;
  totalSpent = 0;
  showBookingForm = false;
  activeTab = 'overview';
  
  selectedCleanerId: number | null = null;
  bookingDate = '';
  durationHours = 2;
  specialInstructions = '';
  errorMessage = '';
  successMessage = '';
  filteredCleaners: Cleaner[] = [];
  selectedDate = '';

  constructor(private http: HttpClient, private authService: AuthService) {}

  ngOnInit() {
    this.loadCustomerData();
    this.loadBookings();
    this.loadAvailableCleaners();
  }

  setActiveTab(tabName: string) {
    this.activeTab = tabName;
  }

  loadCustomerData() {
    const currentUser = this.authService.getCurrentUser();
    if (currentUser) {
      this.http.get<Customer>(`http://localhost:8080/api/customers/${currentUser.id}`, { headers: this.getAuthHeaders() }).subscribe({
        next: (customer) => {
          this.customer = customer;
        },
        error: (error) => {
          console.error('Error loading customer data:', error);
          this.errorMessage = 'Failed to load customer data. Please try again.';
        }
      });
    }
  }

  loadBookings() {
    const currentUser = this.authService.getCurrentUser();
    if (currentUser) {
      this.http.get<Booking[]>(`http://localhost:8080/api/bookings/customer/${currentUser.id}`, { headers: this.getAuthHeaders() }).subscribe({
        next: (bookings) => {
          this.bookings = bookings;
          this.calculateStats();
        },
        error: (error) => {
          console.error('Error loading bookings:', error);
          this.errorMessage = 'Failed to load bookings. Please try again.';
        }
      });
    }
  }

  loadAvailableCleaners() {
    this.http.get<Cleaner[]>('http://localhost:8080/api/cleaners/available', { headers: this.getAuthHeaders() }).subscribe({
      next: (cleaners) => {
        this.availableCleaners = cleaners;
        this.filteredCleaners = cleaners;
      },
      error: (error) => {
        console.error('Error loading cleaners:', error);
        this.errorMessage = 'Failed to load available cleaners. Please try again.';
      }
    });
  }

  calculateStats() {
    this.totalBookings = this.bookings.length;
    this.pendingBookings = this.bookings.filter(b => b.status === 'PENDING').length;
    this.completedBookings = this.bookings.filter(b => b.status === 'COMPLETED').length;
    this.totalSpent = this.bookings
      .filter(b => b.status === 'COMPLETED')
      .reduce((sum, b) => sum + b.totalAmount, 0);
  }

  createBooking() {
    if (!this.selectedCleanerId || !this.bookingDate) {
      this.errorMessage = 'Please select a cleaner and date.';
      return;
    }

    const bookingRequest = {
      customerId: this.customer?.id,
      cleanerId: this.selectedCleanerId,
      bookingDate: this.bookingDate,
      durationHours: this.durationHours,
      specialInstructions: this.specialInstructions,
      serviceAddress: this.customer?.address
    };

    this.http.post('http://localhost:8080/api/bookings', bookingRequest, { headers: this.getAuthHeaders() }).subscribe({
      next: () => {
        this.successMessage = 'Booking created successfully!';
        this.loadBookings();
        this.showBookingForm = false;
        this.resetBookingForm();
        this.filterCleanersByDate();
        
        // Clear success message after 3 seconds
        setTimeout(() => {
          this.successMessage = '';
        }, 3000);
      },
      error: (error) => {
        console.error('Error creating booking:', error);
        if (error.error && error.error.includes('not available')) {
          this.errorMessage = 'This cleaner is not available on the selected date. Please choose another date or cleaner.';
        } else {
          this.errorMessage = 'Failed to create booking. Please try again.';
        }
      }
    });
  }

  cancelBooking(bookingId: number) {
    this.http.put(`http://localhost:8080/api/bookings/${bookingId}/cancel`, {}, { headers: this.getAuthHeaders() }).subscribe({
      next: () => {
        this.successMessage = 'Booking cancelled successfully.';
        this.loadBookings();
        this.filterCleanersByDate();
        
        // Clear success message after 3 seconds
        setTimeout(() => {
          this.successMessage = '';
        }, 3000);
      },
      error: (error) => {
        console.error('Error cancelling booking:', error);
        this.errorMessage = 'Failed to cancel booking. Please try again.';
      }
    });
  }

  resetBookingForm() {
    this.selectedCleanerId = null;
    this.bookingDate = '';
    this.durationHours = 2;
    this.specialInstructions = '';
  }

  formatDate(dateString: string): string {
    return new Date(dateString).toLocaleDateString();
  }

  getStatusClass(status: string): string {
    switch (status) {
      case 'PENDING': return 'bg-warning';
      case 'CONFIRMED': return 'bg-info';
      case 'IN_PROGRESS': return 'bg-primary';
      case 'COMPLETED': return 'bg-success';
      case 'CANCELLED': return 'bg-danger';
      default: return 'bg-secondary';
    }
  }
}
<div class="dashboard-container">
  <div class="row mb-4">
    <div class="col-12">
      <h2><i class="fas fa-home me-2"></i>Customer Dashboard</h2>
      <hr>
    </div>
  </div>

  <!-- Navigation Tabs -->
  <div class="row mb-4">
    <div class="col-12">
      <ul class="nav nav-tabs">
        <li class="nav-item">
          <button class="nav-link" [class.active]="activeTab==='overview'" (click)="setActiveTab('overview')">
            <i class="fas fa-chart-line me-2"></i>Overview
          </button>
        </li>
        <li class="nav-item">
          <button class="nav-link" [class.active]="activeTab==='bookings'" (click)="setActiveTab('bookings')">
            <i class="fas fa-list me-2"></i>My Bookings
          </button>
        </li>
        <li class="nav-item">
          <button class="nav-link" [class.active]="activeTab==='profile'" (click)="setActiveTab('profile')">
            <i class="fas fa-user me-2"></i>Profile
          </button>
        </li>
      </ul>
    </div>
  </div>

  <div class="tab-content">
    <!-- Overview Tab -->
    <div class="tab-pane" [class.active]="activeTab==='overview'">
      <div class="row mb-4">
        <div class="col-md-3" *ngFor="let card of [
          {icon:'calendar-check', title:'Total Bookings', value: totalBookings, cls:'text-primary'},
          {icon:'clock', title:'Pending', value: pendingBookings, cls:'text-warning'},
          {icon:'check-circle', title:'Completed', value: completedBookings, cls:'text-success'},
          {icon:'dollar-sign', title:'Total Spent', value: (totalSpent | currency), cls:'text-info'}
        ]; let i = index">
          <div class="card text-center">
            <div class="card-body">
              <i class="fas fa-{{card.icon}} fa-2x {{card.cls}} mb-2"></i>
              <h5 class="card-title">{{ card.title }}</h5>
              <h3 [ngClass]="card.cls">{{ card.value }}</h3>
            </div>
          </div>
        </div>
      </div>
    </div>

    <!-- Bookings Tab -->
    <div class="tab-pane" [class.active]="activeTab==='bookings'">
      <div class="row">
        <div class="col-md-8">
          <div class="card">
            <div class="card-header d-flex justify-content-between align-items-center">
              <h5><i class="fas fa-list me-2"></i>My Bookings</h5>
              <button class="btn btn-primary btn-sm" (click)="showBookingForm = !showBookingForm">
                <i class="fas fa-plus me-1"></i>New Booking
              </button>
            </div>
            <div class="card-body">
              <div class="table-responsive">
                <table class="table table-hover">
                  <thead>
                    <tr>
                      <th>Date</th>
                      <th>Cleaner</th>
                      <th>Duration</th>
                      <th>Amount</th>
                      <th>Status</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr *ngFor="let booking of bookings">
                      <td>{{ formatDate(booking.bookingDate) }}</td>
                      <td>{{ booking.cleaner.firstName }} {{ booking.cleaner.lastName }}</td>
                      <td>{{ booking.durationHours }}h</td>
                      <td>{{ booking.totalAmount | currency }}</td>
                      <td>
                        <span class="badge" [ngClass]="getStatusClass(booking.status)">
                          {{ booking.status }}
                        </span>
                      </td>
                      <td>
                        <button class="btn btn-sm btn-outline-danger" 
                                *ngIf="booking.status === 'PENDING' || booking.status === 'CONFIRMED'"
                                (click)="cancelBooking(booking.id)">
                          Cancel
                        </button>
                      </td>
                    </tr>
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>

        <div class="col-md-4">
          <div class="card" *ngIf="showBookingForm">
            <div class="card-header">
              <h5><i class="fas fa-plus me-2"></i>Book a Cleaner</h5>
            </div>
            <div class="card-body">
              <form (ngSubmit)="createBooking()">
                <div class="mb-3">
                  <label class="form-label">Select Cleaner</label>
                  <select class="form-select" [(ngModel)]="selectedCleanerId" name="cleaner" required>
                    <option value="">Choose a cleaner...</option>
                    <option *ngFor="let cleaner of availableCleaners" [value]="cleaner.id">
                      {{ cleaner.firstName }} {{ cleaner.lastName }} - {{ cleaner.hourlyRate | currency }}/hr
                    </option>
                  </select>
                </div>
                
                <div class="mb-3">
                  <label class="form-label">Date & Time</label>
                  <input type="datetime-local" class="form-control" [(ngModel)]="bookingDate" name="date" required>
                </div>
                
                <div class="mb-3">
                  <label class="form-label">Duration (hours)</label>
                  <input type="number" class="form-control" [(ngModel)]="durationHours" name="duration" min="1" max="8" required>
                </div>
                
                <div class="mb-3">
                  <label class="form-label">Special Instructions</label>
                  <textarea class="form-control" [(ngModel)]="specialInstructions" name="instructions" rows="3"></textarea>
                </div>
                
                <div class="d-grid">
                  <button type="submit" class="btn btn-primary">Book Now</button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>

    <!-- Profile Tab -->
    <div class="tab-pane" [class.active]="activeTab==='profile'">
      <div class="row">
        <div class="col-md-6">
          <div class="card">
            <div class="card-header">
              <h5><i class="fas fa-user me-2"></i>My Profile</h5>
            </div>
            <div class="card-body">
              <p><strong>Name:</strong> {{ customer?.firstName }} {{ customer?.lastName }}</p>
              <p><strong>Email:</strong> {{ customer?.email }}</p>
              <p><strong>Phone:</strong> {{ customer?.phoneNumber || 'Not provided' }}</p>
              <p><strong>Address:</strong> {{ customer?.address }}</p>
              <p><strong>City:</strong> {{ customer?.city || 'Not provided' }}</p>
              <button class="btn btn-outline-primary btn-sm">Edit Profile</button>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
