import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { HttpClient } from '@angular/common/http';
import { Cleaner, Booking } from '../../../models/user.model';

@Component({
  selector: 'app-cleaner-dashboard',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="row">
      <div class="col-12">
        <h2><i class="fas fa-broom me-2"></i>Cleaner Dashboard</h2>
        <hr>
      </div>
    </div>

    <div class="row mb-4">
      <div class="col-md-3">
        <div class="card text-center">
          <div class="card-body">
            <i class="fas fa-calendar-check fa-2x text-primary mb-2"></i>
            <h5 class="card-title">Total Jobs</h5>
            <h3 class="text-primary">{{ totalJobs }}</h3>
          </div>
        </div>
      </div>
      <div class="col-md-3">
        <div class="card text-center">
          <div class="card-body">
            <i class="fas fa-clock fa-2x text-warning mb-2"></i>
            <h5 class="card-title">Pending</h5>
            <h3 class="text-warning">{{ pendingJobs }}</h3>
          </div>
        </div>
      </div>
      <div class="col-md-3">
        <div class="card text-center">
          <div class="card-body">
            <i class="fas fa-check-circle fa-2x text-success mb-2"></i>
            <h5 class="card-title">Completed</h5>
            <h3 class="text-success">{{ completedJobs }}</h3>
          </div>
        </div>
      </div>
      <div class="col-md-3">
        <div class="card text-center">
          <div class="card-body">
            <i class="fas fa-dollar-sign fa-2x text-info mb-2"></i>
            <h5 class="card-title">Earnings</h5>
            <h3 class="text-info">{{ totalEarnings | currency }}</h3>
          </div>
        </div>
      </div>
    </div>

    <div class="row">
      <div class="col-md-8">
        <div class="card">
          <div class="card-header">
            <h5><i class="fas fa-list me-2"></i>My Jobs</h5>
          </div>
          <div class="card-body">
            <div class="table-responsive">
              <table class="table table-hover">
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Customer</th>
                    <th>Duration</th>
                    <th>Amount</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  <tr *ngFor="let booking of bookings">
                    <td>{{ formatDate(booking.bookingDate) }}</td>
                    <td>{{ booking.customer.firstName }} {{ booking.customer.lastName }}</td>
                    <td>{{ booking.durationHours }}h</td>
                    <td>{{ booking.totalAmount | currency }}</td>
                    <td>
                      <span class="badge" [ngClass]="getStatusClass(booking.status)">
                        {{ booking.status }}
                      </span>
                    </td>
                    <td>
                      <button class="btn btn-sm btn-success me-1" 
                              *ngIf="booking.status === 'PENDING'"
                              (click)="confirmBooking(booking.id)">
                        Confirm
                      </button>
                      <button class="btn btn-sm btn-primary me-1" 
                              *ngIf="booking.status === 'CONFIRMED'"
                              (click)="startBooking(booking.id)">
                        Start
                      </button>
                      <button class="btn btn-sm btn-success me-1" 
                              *ngIf="booking.status === 'IN_PROGRESS'"
                              (click)="completeBooking(booking.id)">
                        Complete
                      </button>
                    </td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        </div>
      </div>

      <div class="col-md-4">
        <div class="card">
          <div class="card-header">
            <h5><i class="fas fa-user me-2"></i>My Profile</h5>
          </div>
          <div class="card-body">
            <p><strong>Name:</strong> {{ cleaner?.firstName }} {{ cleaner?.lastName }}</p>
            <p><strong>Email:</strong> {{ cleaner?.email }}</p>
            <p><strong>Phone:</strong> {{ cleaner?.phoneNumber || 'Not provided' }}</p>
            <p><strong>Address:</strong> {{ cleaner?.address }}</p>
            <p><strong>Hourly Rate:</strong> {{ cleaner?.hourlyRate | currency }}/hr</p>
            <p><strong>Status:</strong> 
              <span class="badge" [ngClass]="getStatusClass(cleaner?.cleanerStatus || '')">
                {{ cleaner?.cleanerStatus }}
              </span>
            </p>
            <button class="btn btn-outline-primary btn-sm me-2">Edit Profile</button>
            <button class="btn btn-outline-secondary btn-sm" (click)="toggleStatus()">
              {{ cleaner?.cleanerStatus === 'AVAILABLE' ? 'Go Offline' : 'Go Online' }}
            </button>
          </div>
        </div>

        <div class="card mt-3">
          <div class="card-header">
            <h5><i class="fas fa-chart-line me-2"></i>Quick Stats</h5>
          </div>
          <div class="card-body">
            <p><strong>Average Rating:</strong> 4.8/5</p>
            <p><strong>Jobs This Month:</strong> {{ jobsThisMonth }}</p>
            <p><strong>Earnings This Month:</strong> {{ earningsThisMonth | currency }}</p>
          </div>
        </div>
      </div>
    </div>
  `,
  styles: []
})
export class CleanerDashboardComponent implements OnInit {
  cleaner: Cleaner | null = null;
  bookings: Booking[] = [];
  totalJobs = 0;
  pendingJobs = 0;
  completedJobs = 0;
  totalEarnings = 0;
  jobsThisMonth = 0;
  earningsThisMonth = 0;

  constructor(private http: HttpClient) {}

  ngOnInit() {
    this.loadCleanerData();
    this.loadBookings();
  }

  loadCleanerData() {
    const cleanerId = 2; // This should come from the logged-in user
    this.http.get<Cleaner>(`http://localhost:8080/api/cleaners/${cleanerId}`).subscribe({
      next: (cleaner) => {
        this.cleaner = cleaner;
      },
      error: (error) => {
        console.error('Error loading cleaner data:', error);
      }
    });
  }

  loadBookings() {
    const cleanerId = 2; // This should come from the logged-in user
    this.http.get<Booking[]>(`http://localhost:8080/api/bookings/cleaner/${cleanerId}`).subscribe({
      next: (bookings) => {
        this.bookings = bookings;
        this.calculateStats();
      },
      error: (error) => {
        console.error('Error loading bookings:', error);
      }
    });
  }

  calculateStats() {
    this.totalJobs = this.bookings.length;
    this.pendingJobs = this.bookings.filter(b => b.status === 'PENDING').length;
    this.completedJobs = this.bookings.filter(b => b.status === 'COMPLETED').length;
    this.totalEarnings = this.bookings
      .filter(b => b.status === 'COMPLETED')
      .reduce((sum, b) => sum + b.totalAmount, 0);
    
    // Calculate monthly stats (simplified)
    const currentMonth = new Date().getMonth();
    const currentYear = new Date().getFullYear();
    const monthlyBookings = this.bookings.filter(b => {
      const bookingDate = new Date(b.bookingDate);
      return bookingDate.getMonth() === currentMonth && bookingDate.getFullYear() === currentYear;
    });
    
    this.jobsThisMonth = monthlyBookings.length;
    this.earningsThisMonth = monthlyBookings
      .filter(b => b.status === 'COMPLETED')
      .reduce((sum, b) => sum + b.totalAmount, 0);
  }

  confirmBooking(bookingId: number) {
    this.http.put(`http://localhost:8080/api/bookings/${bookingId}/confirm`, {}).subscribe({
      next: () => {
        this.loadBookings();
      },
      error: (error) => {
        console.error('Error confirming booking:', error);
      }
    });
  }

  startBooking(bookingId: number) {
    this.http.put(`http://localhost:8080/api/bookings/${bookingId}/start`, {}).subscribe({
      next: () => {
        this.loadBookings();
      },
      error: (error) => {
        console.error('Error starting booking:', error);
      }
    });
  }

  completeBooking(bookingId: number) {
    this.http.put(`http://localhost:8080/api/bookings/${bookingId}/complete`, {}).subscribe({
      next: () => {
        this.loadBookings();
      },
      error: (error) => {
        console.error('Error completing booking:', error);
      }
    });
  }

  toggleStatus() {
    if (!this.cleaner) return;
    
    const newStatus = this.cleaner.cleanerStatus === 'AVAILABLE' ? 'OFFLINE' : 'AVAILABLE';
    this.http.put(`http://localhost:8080/api/cleaners/${this.cleaner.id}/status?status=${newStatus}`, {}).subscribe({
      next: () => {
        this.loadCleanerData();
      },
      error: (error) => {
        console.error('Error updating status:', error);
      }
    });
  }

  formatDate(dateString: string): string {
    return new Date(dateString).toLocaleDateString();
  }

  getStatusClass(status: string): string {
    switch (status) {
      case 'PENDING': return 'bg-warning';
      case 'CONFIRMED': return 'bg-info';
      case 'IN_PROGRESS': return 'bg-primary';
      case 'COMPLETED': return 'bg-success';
      case 'CANCELLED': return 'bg-danger';
      case 'AVAILABLE': return 'bg-success';
      case 'BUSY': return 'bg-warning';
      case 'OFFLINE': return 'bg-secondary';
      default: return 'bg-secondary';
    }
  }
}
